using Brugnner.API.Core.Contracts.Repositories;
using Brugnner.API.Core.Contracts.Services;
using Brugnner.API.Core.Domain;
using Brugnner.API.Core.Extensions;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Logging;
using System;
using System.Collections.Generic;
using System.Linq;

namespace Brugnner.API.Infrastructure.Services
{
    /// <summary>
    /// Service tasked with the database initialization.
    /// </summary>
    public class DbInitializerService : IDbInitializerService
    {
        private readonly Random _random = new Random();
        private readonly IServiceScopeFactory _scopeFactory;
        private readonly ILogger<DbInitializerService> _logger;
        private readonly IPostRepository _postRepository;

        /// <summary>
        /// Creates a new instance of <see cref="DbInitializerService"/>.
        /// </summary>
        /// <param name="scopeFactory">Scope factory.</param>
        /// <param name="logger">Logger.</param>
        /// <param name="postRepository">Post repository.</param>
        public DbInitializerService(IServiceScopeFactory scopeFactory, ILogger<DbInitializerService> logger, IPostRepository postRepository)
        {
            _scopeFactory = scopeFactory.ThrowIfNull(nameof(scopeFactory));
            _logger = logger.ThrowIfNull(nameof(logger));
            _postRepository = postRepository.ThrowIfNull(nameof(postRepository));
        }

        /// <summary>
        /// Adds some data to the database.
        /// </summary>
        public void SeedData()
        {
            SeedPosts();
        }

        private void SeedPosts()
        {
            if (_postRepository.GetEntitiesFilePaths().Count() == 0)
            {
                _logger.LogInformation("Seeding posts");

                var posts = new List<Post>();

                posts.Add(new Post()
                {
                    Id = Guid.NewGuid(),
                    Title = "Blues",
                    Excerpt = "The term Blues may have come from 'blue devils', meaning melancholy and sadness; an early use of the term in this sense is in George Colman's one-act farce Blue Devils (1798).",
                    Content = @"<p><strong>Blues</strong>&nbsp;is a&nbsp;<a href=\""https://en.wikipedia.org/wiki/Music_genre\"" title=\""Music genre\"">music genre</a><sup><a href=\""https://en.wikipedia.org/wiki/Blues#cite_note-3\"">[3]</a></sup>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Musical_form\"" title=\""Musical form\"">musical form</a>&nbsp;which was originated in the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Deep_South\"" title=\""Deep South\"">Deep South</a>&nbsp;of the United States around the 1870s by African-Americans from roots in&nbsp;<a href=\""https://en.wikipedia.org/wiki/Music_of_Africa\"" title=\""Music of Africa\"">African musical traditions</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Plantation-era_songs\"" title=\""Plantation-era songs\"">African-American work songs</a>, and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Spiritual_(music)\"" title=\""Spiritual (music)\"">spirituals</a>. Blues incorporated&nbsp;<a href=\""https://en.wikipedia.org/wiki/Spiritual_(music)\"" title=\""Spiritual (music)\"">spirituals</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Work_song\"" title=\""Work song\"">work songs</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Field_holler\"" title=\""Field holler\"">field hollers</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Ring_shout\"" title=\""Ring shout\"">shouts</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Chant\"" title=\""Chant\"">chants</a>, and rhymed simple narrative&nbsp;<a href=\""https://en.wikipedia.org/wiki/Ballad_(music)\"" title=\""Ballad (music)\"">ballads</a>.<sup><a href=\""https://en.wikipedia.org/wiki/Blues#cite_note-The_Evolution_of_Differing_Blues_Styles-2\"">[2]</a></sup>&nbsp;The blues form, ubiquitous in&nbsp;<a href=\""https://en.wikipedia.org/wiki/Jazz\"" title=\""Jazz\"">jazz</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Rhythm_and_blues\"" title=\""Rhythm and blues\"">rhythm and blues</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Rock_and_roll\"" title=\""Rock and roll\"">rock and roll</a>, is characterized by the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Call_and_response_(music)\"" title=\""Call and response (music)\"">call-and-response</a>&nbsp;pattern, the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Blues_scale\"" title=\""Blues scale\"">blues scale</a>&nbsp;and specific&nbsp;<a href=\""https://en.wikipedia.org/wiki/Chord_progression\"" title=\""Chord progression\"">chord progressions</a>, of which the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Twelve-bar_blues\"" title=\""Twelve-bar blues\"">twelve-bar blues</a>&nbsp;is the most common.&nbsp;<a href=\""https://en.wikipedia.org/wiki/Blue_note\"" title=\""Blue note\"">Blue notes</a>&nbsp;(or &quot;worried notes&quot;), usually thirds, fifths or sevenths flattened in&nbsp;<a href=\""https://en.wikipedia.org/wiki/Pitch_(music)\"" title=\""Pitch (music)\"">pitch</a>&nbsp;are also an essential part of the sound. Blues&nbsp;<a href=\""https://en.wikipedia.org/wiki/Shuffle_note\"" title=\""Shuffle note\"">shuffles</a>&nbsp;or&nbsp;<a href=\""https://en.wikipedia.org/wiki/Walking_bass\"" title=\""Walking bass\"">walking bass</a>&nbsp;reinforce the trance-like rhythm and form a repetitive effect known as the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Groove_(popular_music)\"" title=\""Groove (popular music)\"">groove</a>.</p>

        <p>Blues as a genre is also characterized by its lyrics, bass lines, and instrumentation. Early&nbsp;<a href=\""https://en.wikipedia.org/wiki/Traditional_blues_verse\"" title=\""Traditional blues verse\"">traditional blues verses</a>&nbsp;consisted of a single line repeated four times. It was only in the first decades of the 20th century that the most common current structure became standard: the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Bar_form\"" title=\""Bar form\"">AAB pattern</a>, consisting of a line sung over the four first bars, its repetition over the next four, and then a longer concluding line over the last bars. Early blues frequently took the form of a loose narrative, often relating the racial discrimination and other challenges experienced by African-Americans.</p>

        <p>Many elements, such as the call-and-response format and the use of blue notes, can be traced back to the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Music_of_Africa\"" title=\""Music of Africa\"">music of Africa</a>. The origins of the blues are also closely related to the religious music of the Afro-American community, the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Spiritual_(music)\"" title=\""Spiritual (music)\"">spirituals</a>. The first appearance of the blues is often dated to after the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Abolitionism_in_the_United_States\"" title=\""Abolitionism in the United States\"">ending of slavery</a>&nbsp;and, later, the development of&nbsp;<a href=\""https://en.wikipedia.org/wiki/Juke_joint\"" title=\""Juke joint\"">juke joints</a>. It is associated with the newly acquired freedom of the former slaves. Chroniclers began to report about blues music at the dawn of the&nbsp;<a href=\""https://en.wikipedia.org/wiki/20th_century\"" title=\""20th century\"">20th century</a>. The first publication of blues sheet music was in 1908. Blues has since evolved from unaccompanied vocal music and oral traditions of slaves into a wide variety of styles and subgenres. Blues&nbsp;<a href=\""https://en.wikipedia.org/wiki/List_of_blues_genres\"" title=\""List of blues genres\"">subgenres</a>&nbsp;include&nbsp;<a href=\""https://en.wikipedia.org/wiki/Country_blues\"" title=\""Country blues\"">country blues</a>, such as&nbsp;<a href=\""https://en.wikipedia.org/wiki/Delta_blues\"" title=\""Delta blues\"">Delta blues</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Piedmont_blues\"" title=\""Piedmont blues\"">Piedmont blues</a>, as well as urban blues styles such as&nbsp;<a href=\""https://en.wikipedia.org/wiki/Chicago_blues\"" title=\""Chicago blues\"">Chicago blues</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/West_Coast_blues\"" title=\""West Coast blues\"">West Coast blues</a>.&nbsp;<a href=\""https://en.wikipedia.org/wiki/World_War_II\"" title=\""World War II\"">World War II</a>&nbsp;marked the transition from acoustic to&nbsp;<a href=\""https://en.wikipedia.org/wiki/Electric_blues\"" title=\""Electric blues\"">electric blues</a>&nbsp;and the progressive opening of blues music to a wider audience, especially white listeners. In the 1960s and 1970s, a hybrid form called&nbsp;<a href=\""https://en.wikipedia.org/wiki/Blues_rock\"" title=\""Blues rock\"">blues rock</a>&nbsp;developed, which blended blues styles with&nbsp;<a href=\""https://en.wikipedia.org/wiki/Rock_music\"" title=\""Rock music\"">rock music</a>.</p>",
                    Slug = "blues",
                    CreatedAt = DateTime.Now.AddDays(GetRandom(1, 365) * -1),
                    UpdatedAt = DateTime.Now.AddDays(GetRandom(1, 30) * -1),
                    IsPublished = true,
                    Tags = new List<Tag>() { new Tag("blues"), new Tag("music") }
                });

                posts.Add(new Post()
                {
                    Id = Guid.NewGuid(),
                    Title = "Rock",
                    Excerpt = "The sound of rock is traditionally centered on the amplified electric guitar, which emerged in its modern form in the 1950s.",
                    Content = @"<p><strong>Rock music</strong>&nbsp;is a broad genre of&nbsp;<a href=\""https://en.wikipedia.org/wiki/Popular_music\"" title=\""Popular music\"">popular music</a>&nbsp;that originated as &quot;<a href=\""https://en.wikipedia.org/wiki/Rock_and_roll\"" title=\""Rock and roll\"">rock and roll</a>&quot; in the&nbsp;<a href=\""https://en.wikipedia.org/wiki/United_States\"" title=\""United States\"">United States</a>&nbsp;in the early 1950s, and developed into a range of different styles in the 1960s and later, particularly in the United States and the United Kingdom.<sup><a href=\""https://en.wikipedia.org/wiki/Rock_music#cite_note-studwell-1\"">[1]</a></sup><sup><a href=\""https://en.wikipedia.org/wiki/Rock_music#cite_note-2\"">[2]</a></sup>&nbsp;It has its roots in 1940s and 1950s rock and roll, a style which drew heavily from the genres of&nbsp;<a href=\""https://en.wikipedia.org/wiki/Blues\"" title=\""Blues\"">blues</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Rhythm_and_blues\"" title=\""Rhythm and blues\"">rhythm and blues</a>, and from&nbsp;<a href=\""https://en.wikipedia.org/wiki/Country_music\"" title=\""Country music\"">country music</a>. Rock music also drew strongly from a number of other genres such as&nbsp;<a href=\""https://en.wikipedia.org/wiki/Electric_blues\"" title=\""Electric blues\"">electric blues</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Folk_music\"" title=\""Folk music\"">folk</a>, and incorporated influences from&nbsp;<a href=\""https://en.wikipedia.org/wiki/Jazz\"" title=\""Jazz\"">jazz</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Classical_music\"" title=\""Classical music\"">classical</a>&nbsp;and other musical styles. Musically, rock has centered on the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Electric_guitar\"" title=\""Electric guitar\"">electric guitar</a>, usually as part of a&nbsp;<a href=\""https://en.wikipedia.org/wiki/Rock_group\"" title=\""Rock group\"">rock group</a>&nbsp;with&nbsp;<a href=\""https://en.wikipedia.org/wiki/Electric_bass\"" title=\""Electric bass\"">electric bass</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Drum_kit\"" title=\""Drum kit\"">drums</a>, and one or more&nbsp;<a href=\""https://en.wikipedia.org/wiki/Singer\"" title=\""Singer\"">singers</a>. Usually, rock is&nbsp;<a href=\""https://en.wikipedia.org/wiki/Song\"" title=\""Song\"">song</a>-based music with a&nbsp;<a href=\""https://en.wikipedia.org/wiki/Time_signature\"" title=\""Time signature\"">4/4 time signature</a>&nbsp;using a&nbsp;<a href=\""https://en.wikipedia.org/wiki/Verse%E2%80%93chorus_form\"" title=\""Verse–chorus form\"">verse&ndash;chorus form</a>, but the genre has become extremely diverse. Like&nbsp;<a href=\""https://en.wikipedia.org/wiki/Pop_music\"" title=\""Pop music\"">pop music</a>, lyrics often stress romantic love but also address a wide variety of other themes that are frequently social or political.</p>

                    <p>By the late 1960s &quot;classic rock&quot;<sup><a href=\""https://en.wikipedia.org/wiki/Rock_music#cite_note-studwell-1\"">[1]</a></sup>&nbsp;period, a number of distinct rock music subgenres had emerged, including hybrids like&nbsp;<a href=\""https://en.wikipedia.org/wiki/Blues_rock\"" title=\""Blues rock\"">blues rock</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Folk_rock\"" title=\""Folk rock\"">folk rock</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Country_rock\"" title=\""Country rock\"">country rock</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Southern_rock\"" title=\""Southern rock\"">southern rock</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Raga_rock\"" title=\""Raga rock\"">raga rock</a>, and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Jazz-rock\"" title=\""Jazz-rock\"">jazz-rock</a>, many of which contributed to the development of&nbsp;<a href=\""https://en.wikipedia.org/wiki/Psychedelic_rock\"" title=\""Psychedelic rock\"">psychedelic rock</a>, which was influenced by the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Counterculture_of_the_1960s\"" title=\""Counterculture of the 1960s\"">countercultural</a>&nbsp;<a href=\""https://en.wikipedia.org/wiki/Psychedelia\"" title=\""Psychedelia\"">psychedelic and hippie scene</a>. New genres that emerged included&nbsp;<a href=\""https://en.wikipedia.org/wiki/Progressive_rock\"" title=\""Progressive rock\"">progressive rock</a>, which extended the artistic elements;&nbsp;<a href=\""https://en.wikipedia.org/wiki/Glam_rock\"" title=\""Glam rock\"">glam rock</a>, which highlighted showmanship and visual style; and the diverse and enduring subgenre of&nbsp;<a href=\""https://en.wikipedia.org/wiki/Heavy_metal_music\"" title=\""Heavy metal music\"">heavy metal</a>, which emphasized volume, power, and speed. In the second half of the 1970s,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Punk_rock\"" title=\""Punk rock\"">punk rock</a>&nbsp;reacted by producing stripped-down, energetic social and political critiques. Punk was an influence in the 1980s on&nbsp;<a href=\""https://en.wikipedia.org/wiki/New_wave_music\"" title=\""New wave music\"">new wave</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Post-punk\"" title=\""Post-punk\"">post-punk</a>&nbsp;and eventually&nbsp;<a href=\""https://en.wikipedia.org/wiki/Alternative_rock\"" title=\""Alternative rock\"">alternative rock</a>. From the 1990s alternative rock began to dominate rock music and break into the mainstream in the form of&nbsp;<a href=\""https://en.wikipedia.org/wiki/Grunge\"" title=\""Grunge\"">grunge</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Britpop\"" title=\""Britpop\"">Britpop</a>, and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Indie_rock\"" title=\""Indie rock\"">indie rock</a>. Further fusion subgenres have since emerged, including&nbsp;<a href=\""https://en.wikipedia.org/wiki/Pop_punk\"" title=\""Pop punk\"">pop punk</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Electronic_rock\"" title=\""Electronic rock\"">electronic rock</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Rap_rock\"" title=\""Rap rock\"">rap rock</a>, and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Rap_metal\"" title=\""Rap metal\"">rap metal</a>, as well as conscious attempts to revisit rock&#39;s history, including the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Garage_rock\"" title=\""Garage rock\"">garage rock</a>/<a href=\""https://en.wikipedia.org/wiki/Post-punk_revival\"" title=\""Post-punk revival\"">post-punk</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Techno-pop\"" title=\""Techno-pop\"">techno-pop</a>&nbsp;revivals at the beginning of the 2000s.</p>

                    <p>Rock music has also embodied and served as the vehicle for cultural and social movements, leading to major subcultures including&nbsp;<a href=\""https://en.wikipedia.org/wiki/Mod_(subculture)\"" title=\""Mod (subculture)\"">mods</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Rocker_(subculture)\"" title=\""Rocker (subculture)\"">rockers</a>&nbsp;in the UK and the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Hippie\"" title=\""Hippie\"">hippie</a>&nbsp;counterculture that spread out from San Francisco in the US in the 1960s. Similarly, 1970s&nbsp;<a href=\""https://en.wikipedia.org/wiki/Punk_culture\"" title=\""Punk culture\"">punk culture</a>&nbsp;spawned the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Goth_subculture\"" title=\""Goth subculture\"">goth</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Punk_subculture\"" title=\""Punk subculture\"">punk</a>, and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Emo\"" title=\""Emo\"">emo</a>&nbsp;subcultures. Inheriting the folk tradition of the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Protest_song\"" title=\""Protest song\"">protest song</a>, rock music has been associated with political activism as well as changes in social attitudes to race, sex and drug use, and is often seen as an expression of youth revolt against adult&nbsp;<a href=\""https://en.wikipedia.org/wiki/Consumerism\"" title=\""Consumerism\"">consumerism</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Conformity\"" title=\""Conformity\"">conformity</a>.</p>",
                    Slug = "rock",
                    CreatedAt = DateTime.Now.AddDays(GetRandom(1, 365) * -1),
                    UpdatedAt = DateTime.Now.AddDays(GetRandom(1, 30) * -1),
                    IsPublished = true,
                    Tags = new List<Tag>() { new Tag("rock"), new Tag("music") }
                });

                posts.Add(new Post()
                {
                    Id = Guid.NewGuid(),
                    Title = "Jazz",
                    Excerpt = "The origin of the word jazz has resulted in considerable research, and its history is well documented.",
                    Content = @"<p><strong>Jazz</strong>&nbsp;is a&nbsp;<a href=\""https://en.wikipedia.org/wiki/Music_genre\"" title=\""Music genre\"">music genre</a>&nbsp;that originated in the&nbsp;<a href=\""https://en.wikipedia.org/wiki/African-American\"" title=\""African-American\"">African-American</a>&nbsp;communities of&nbsp;<a href=\""https://en.wikipedia.org/wiki/New_Orleans\"" title=\""New Orleans\"">New Orleans</a>, United States.<sup><a href=\""https://en.wikipedia.org/wiki/Jazz#cite_note-1\"">[1]</a></sup>&nbsp;It originated in the late 19th and early 20th centuries, and developed from roots in&nbsp;<a href=\""https://en.wikipedia.org/wiki/Blues\"" title=\""Blues\"">blues</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Ragtime\"" title=\""Ragtime\"">ragtime</a>.<sup><a href=\""https://en.wikipedia.org/wiki/Jazz#cite_note-2\"">[2]</a></sup>&nbsp;Jazz is seen by many as &quot;America&#39;s classical music&quot;.<sup><a href=\""https://en.wikipedia.org/wiki/Jazz#cite_note-:0-3\"">[3]</a></sup>&nbsp;Since the 1920s&nbsp;<a href=\""https://en.wikipedia.org/wiki/Jazz_Age\"" title=\""Jazz Age\"">Jazz Age</a>, jazz has become recognized as a major form of musical expression. It then emerged in the form of independent&nbsp;<a href=\""https://en.wikipedia.org/wiki/Traditional_music\"" title=\""Traditional music\"">traditional</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Popular_music\"" title=\""Popular music\"">popular musical</a>&nbsp;styles, all linked by the common bonds of African-American and&nbsp;<a href=\""https://en.wikipedia.org/wiki/European-American\"" title=\""European-American\"">European-American</a>&nbsp;musical parentage with a performance orientation.<sup><a href=\""https://en.wikipedia.org/wiki/Jazz#cite_note-Hennessey-4\"">[4]</a></sup>&nbsp;Jazz is characterized by&nbsp;<a href=\""https://en.wikipedia.org/wiki/Swung_note\"" title=\""Swung note\"">swing</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Blue_note\"" title=\""Blue note\"">blue notes</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Call_and_response_(music)\"" title=\""Call and response (music)\"">call and response vocals</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Polyrhythm\"" title=\""Polyrhythm\"">polyrhythms</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Improvisation\"" title=\""Improvisation\"">improvisation</a>. Jazz has roots in&nbsp;<a href=\""https://en.wikipedia.org/wiki/Music_of_Africa\"" title=\""Music of Africa\"">West African cultural and musical expression</a>, and in&nbsp;<a href=\""https://en.wikipedia.org/wiki/African-American_music\"" title=\""African-American music\"">African-American music traditions</a>&nbsp;including&nbsp;<a href=\""https://en.wikipedia.org/wiki/Blues\"" title=\""Blues\"">blues</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Ragtime\"" title=\""Ragtime\"">ragtime</a>, as well as European military band music.<sup><a href=\""https://en.wikipedia.org/wiki/Jazz#cite_note-5\"">[5]</a></sup>&nbsp;Intellectuals around the world have hailed jazz as &quot;one of America&#39;s original art forms&quot;.<sup><a href=\""https://en.wikipedia.org/wiki/Jazz#cite_note-6\"">[6]</a></sup></p>

                    <p>As jazz spread around the world, it drew on national, regional, and local musical cultures, which gave rise to different styles.&nbsp;<a href=\""https://en.wikipedia.org/wiki/Dixieland\"" title=\""Dixieland\"">New Orleans jazz</a>&nbsp;began in the early 1910s, combining earlier brass-band marches, French&nbsp;<a href=\""https://en.wikipedia.org/wiki/Quadrille\"" title=\""Quadrille\"">quadrilles</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Biguine\"" title=\""Biguine\"">biguine</a>, ragtime and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Blues\"" title=\""Blues\"">blues</a>&nbsp;with collective&nbsp;<a href=\""https://en.wikipedia.org/wiki/Polyphony\"" title=\""Polyphony\"">polyphonic</a>&nbsp;<a href=\""https://en.wikipedia.org/wiki/Improvisation\"" title=\""Improvisation\"">improvisation</a>. In the 1930s, heavily arranged dance-oriented&nbsp;<a href=\""https://en.wikipedia.org/wiki/Swing_(music)\"" title=\""Swing (music)\"">swing</a>&nbsp;<a href=\""https://en.wikipedia.org/wiki/Big_band\"" title=\""Big band\"">big bands</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Kansas_City_jazz\"" title=\""Kansas City jazz\"">Kansas City jazz</a>, a hard-swinging, bluesy, improvisational style and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Gypsy_jazz\"" title=\""Gypsy jazz\"">Gypsy jazz</a>&nbsp;(a style that emphasized&nbsp;<a href=\""https://en.wikipedia.org/wiki/Bal-musette\"" title=\""Bal-musette\"">musette</a>&nbsp;waltzes) were the prominent styles.&nbsp;<a href=\""https://en.wikipedia.org/wiki/Bebop\"" title=\""Bebop\"">Bebop</a>&nbsp;emerged in the 1940s, shifting jazz from danceable popular music toward a more challenging &quot;musician&#39;s music&quot; which was played at faster tempos and used more chord-based improvisation.&nbsp;<a href=\""https://en.wikipedia.org/wiki/Cool_jazz\"" title=\""Cool jazz\"">Cool jazz</a>&nbsp;developed near the end of the 1940s, introducing calmer, smoother sounds and long, linear melodic lines.</p>

                    <p>The 1950s saw the emergence of&nbsp;<a href=\""https://en.wikipedia.org/wiki/Free_jazz\"" title=\""Free jazz\"">free jazz</a>, which explored playing without regular meter, beat and formal structures, and in the mid-1950s,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Hard_bop\"" title=\""Hard bop\"">hard bop</a>&nbsp;emerged, which introduced influences from&nbsp;<a href=\""https://en.wikipedia.org/wiki/Rhythm_and_blues\"" title=\""Rhythm and blues\"">rhythm and blues</a>,&nbsp;<a href=\""https://en.wikipedia.org/wiki/Gospel_music\"" title=\""Gospel music\"">gospel</a>, and blues, especially in the saxophone and piano playing.&nbsp;<a href=\""https://en.wikipedia.org/wiki/Modal_jazz\"" title=\""Modal jazz\"">Modal jazz</a>&nbsp;developed in the late 1950s, using the&nbsp;<a href=\""https://en.wikipedia.org/wiki/Musical_mode\"" title=\""Musical mode\"">mode</a>, or musical scale, as the basis of musical structure and improvisation.&nbsp;<a href=\""https://en.wikipedia.org/wiki/Jazz_fusion\"" title=\""Jazz fusion\"">Jazz-rock fusion</a>&nbsp;appeared in the late 1960s and early 1970s, combining jazz improvisation with&nbsp;<a href=\""https://en.wikipedia.org/wiki/Rock_music\"" title=\""Rock music\"">rock music</a>&#39;s rhythms, electric instruments, and highly amplified stage sound. In the early 1980s, a commercial form of jazz fusion called&nbsp;<a href=\""https://en.wikipedia.org/wiki/Smooth_jazz\"" title=\""Smooth jazz\"">smooth jazz</a>&nbsp;became successful, garnering significant radio airplay. Other styles and genres abound in the 2000s, such as&nbsp;<a href=\""https://en.wikipedia.org/wiki/Latin_jazz\"" title=\""Latin jazz\"">Latin</a>&nbsp;and&nbsp;<a href=\""https://en.wikipedia.org/wiki/Afro-Cuban_jazz\"" title=\""Afro-Cuban jazz\"">Afro-Cuban jazz</a>.</p>",
                    Slug = "jazz",
                    CreatedAt = DateTime.Now.AddDays(GetRandom(1, 365) * -1),
                    UpdatedAt = DateTime.Now.AddDays(GetRandom(1, 30) * -1),
                    IsPublished = true,
                    Tags = new List<Tag>() { new Tag("jazz"), new Tag("music") }
                });

